mapper.insert:
INSERT INTO USERS(
    USER_NO
   ,USER_ID
   ,USER_PW
   ,USER_NAME
   ,USER_JUMIN
   ,PHONE
   ,EMAIL
   ,RGST_TIME
   ,UDT_TIME
   ,GUBN
) VALUES (
	(SELECT NVL(MAX(USER_NO),0)+1 FROM USERS)
   ,#{userId}
   ,#{userPw}
   ,#{userName}
   ,#{userJumin}
   ,#{phone}
   ,#{email}
   ,#{rgstTime}
   ,#{udtTime}
   ,#{gubn}
)

mapper.select:
SELECT COUNT(*)
  FROM USERS
 WHERE USER_ID = #{userId}

SELECT *
  FROM USERS
 WHERE USER_NAME = #{userName}
   AND EMAIL = #{email}

SELECT COUNT(*)
  FROM USERS
 WHERE USER_NAME = #{userName}
   AND EMAIL = #{email}

mapper.update:
UPDATE USERS 
   SET USER_PW = #{userPw} 
 WHERE USER_NAME = #{userName}
   AND EMAIL = #{email}

mapper.select:
SELECT COUNT(*)
  FROM USERS
 WHERE USER_ID = #{userId}
   AND USER_PW = #{userPw}
